import { useSelector } from "react-redux";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const userInfo = useSelector(state=> state.userInfo)
  const [editBio, setEdit] = useState(false)
  const username = useSelector(state=> state.userInfo.username)

  const handleBioEdit = async(e) => {
    e.preventDefault();
    let bio = e.target.value;
    let body = JSON.stringify({username, bio})
    try {
      const response = await fetch("http://localhost:3000/editBio", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body,
      });
      if (!response.ok) {
        throw new Error(`Failed to fetch: ${response.statusText}`);
      }
      const user = response.json().then(data=>{
        store.dispatch(confirmedLogin(true));
        console.log("---------user signed in ", data);
        handleConfirmed()
      })

    } catch (error) {
      console.error("Error during fetch:", error);
    }
  }

  return (
    <div className="profile"> 
    <div>
      <a>{userInfo.username}</a>
      <a>{userInfo.bio}</a>
      <input>{editBio ? <form onSubmit={handleBioEdit}><input placeholder="Edit bio"></input><button >Submit</button></form> : null}</input>
    </div>
      <button onClick={setEdit(true)}>Edit bio</button>


    </div>
  )
}

export default Profile
