import { useSelector } from "react-redux";
import React, { useState } from "react";
import { editBio } from "./Redux/isLoggedIn";
import { store } from "./Redux/store";
const Profile = () => {
  const userInfo = useSelector(state=> state.userInfo)
  const [changeBio, setEdit] = useState(false)
  const username = useSelector(state=> state.userInfo.username)

  const handleBioEdit = async(e) => {
    e.preventDefault();
    let bio = e.target.value;
    let body = JSON.stringify({username, bio})
    try {
      const response = await fetch("http://localhost:3000/editBio", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body,
      });
      if (!response.ok) {
        throw new Error(`Failed to fetch: ${response.statusText}`);
      }
      const user = response.json().then(data=>{
        store.dispatch(editBio(data.newBio));
        console.log("User update ", data);
        setEdit(false)

      })

    } catch (error) {
      console.error("Error during fetch:", error);
    }
  }
  console.log(userInfo)

  return (
    <div className="profile"> 
    <div>
      <p>{userInfo.username}</p>
      <p>{userInfo.bio}</p>
      {editBio ? <form onSubmit={(e)=> handleBioEdit(e)}><input placeholder="Edit bio"></input><button >Submit</button></form> : null}
    </div>
      <button onClick={() => setEdit(true)}>Edit bio</button>


    </div>
  )
}

export default Profile
