import { useSelector } from "react-redux";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const userInfo = useSelector(state=> state.userInfo)
  const [editBio, setEdit] = useState(false)

  const handleBioEdit = (e) => {
    e.preventDefault();
    let bio = e.target.value;

  }

  return (
    <div className="profile"> 
    <div>
      <a>{userInfo.username}</a>
      <a>{userInfo.bio}</a>
      <input>{editBio ? <form onSubmit={handleBioEdit}><input placeholder="Edit bio"></input><button >Submit</button></form> : null}</input>
    </div>
      <button onClick={setEdit(true)}>Edit bio</button>


    </div>
  )
}

export default Profile
